var recordtypeResult1 = sforce.connection.query("Select id FROM RecordType WHERE Name='Salesforce.com Change Request' and SobjectType='NI_Change_Control__c'");

var recordTypeID = '';


if(recordtypeResult1 != ''){
 recordTypeID = recordtypeResult1.records.Id;
}

var sales_force_app ="Salesforce";
var request_summary ="{!NI_Project_Backlog__c.Name}";
var request_details = "{!JSENCODE(NI_Project_Backlog__c.Project_Details__c)}";
var change_steps = "Deploy related changes from the Development Sandbox.";
var test_plan = "Confirm standard work flows are functioning as designed per the related 'NI Project Backlog' record.";
var rollback_plan = "Undeploy related functionality";
var change_reason = "Additional Functionality";

var delployment_owner="Stuart Emery";
var change_type="{!NI_Project_Backlog__c.Change_Type__c}";
var project_title = "{!NI_Project_Backlog__c.Name}";
var project_id = "{!NI_Project_Backlog__c.Id}";


var today=new Date();
var in_a_week=new Date().setDate(today.getDate()+7);
var d = new Date(in_a_week);
var month = (d.getMonth()+1);
var day = (d.getDate());
var year = (d.getFullYear());

var production_start_date =  month + "/" + day + "/" + year;
//alert(production_start_date);
//ent=01I600000006SsE&nooverride=1&RecordType='+recordTypeID+'&save_new_url=/a0s/

var main_url = '/a0s/e?CF00N60000002gM4m='+project_title +'&retURL=/'+project_id+'&'+'&RecordType='+recordTypeID+'&';

var url = main_url+'00N60000002FV0i=Salesforce&00N60000002FGsd='+request_summary+'&00N60000002FGsT='+change_steps+'&00N60000002FGsh='+test_plan+'&00N60000002FGse='+rollback_plan +'&00N60000002FGsR='+change_reason+'&CF00N60000002FGsa='+delployment_owner+'&00N60000002FGsb=Medium';

if(request_details != ''){
url+='&00N60000002FGsc='+request_details;
}
//mm/dd/yyyy
if(production_start_date != ''){
url+='&00N60000002FGsX='+production_start_date;
}

var FromPickListField  ="{!NI_Project_Backlog__c.Beneficiaries__c}"; 
var ToPickListFieldName ="00N60000002FGsZ";  


var pairs =  FromPickListField.split(";"); 
if(pairs != ''){
  var Fields = "";
  for (var i=0;i<pairs.length;i++) 
  {  
   var ThisValue = pairs[i]; 
   Fields =Fields + ToPickListFieldName+  "=" + ThisValue + "&"; 
  } 
  url+='&' +Fields;
}
var FromFieldChangetype  = change_type; 
var ToChangeType ="00N60000002FGsU";  


var pairs =  FromFieldChangetype.split(";"); 
if(pairs != ''){
  var Fields = "";
  for (var i=0;i<pairs.length;i++) 
  {  
   var ThisValue = pairs[i]; 
   Fields =Fields + ToChangeType +  "=" + ThisValue + "&"; 
  } 
  url+='&' +Fields;
}

window.location.href = url;
